import React, { useState } from "react";
import "./TodoList.css";

function Exzamen() {
  const [color, setColor] = useState("");
  const handleColorChange = () => {
    setColor(color); // Update the color state with the input value
  };
  return (
    <div>
      <div className="app" style={{ background: `${color}` }}></div>
      <input className="input" type="text" value={color} onChange={(e) => setColor(e.target.value)} />
      <button>Add color</button>
    </div>
  );
}

export default Exzamen;
