import React, { useState } from "react";
import "./TodoList.css";

function Exzamen() {
  const [color, setColor] = useState("");
  const [text, setText] = useState("");
  const [savedColors, setSavedColors] = useState([]);
  const handleColorChange = () => {
    if (text) {
      setSavedColors([...savedColors, color]);
      setColor(text); // Update the color state with the input value
    }
  };
  return (
    <div>
      <div className="app" style={{ background: color }}></div>
      <input
        className="input"
        type="text"
        placeholder="Enter a color"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button onClick={handleColorChange}>Add color</button>
      <div className="saved-colors">
        <h2>Saved Colors:</h2>
        <ul>
          {savedColors.map((savedColor, index) => (
            <li key={index} style={{ background: savedColor }}></li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Exzamen;
