import React, { Component } from "react";
import "./App.css";

class Counter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "Hello Kyrgyzstan",
      count: 0,
      stepCount: 0,
    };
  }

  handleIncrement = () => {
    this.setState((prevState) => ({
      count: prevState.count + 1,
    }));
  };

  handleDecrement = () => {
    this.setState((prevState) => ({
      count: prevState.count - 1,
    }));
  };

  handleIncrementStep = () => {
    this.setState((prevState) => ({
      stepCount: prevState.stepCount + 10,
    }));
  };

  render() {
    return (
      <div>
        <h2>{this.state.count}</h2>
        <button onClick={this.handleIncrement}>increment</button>
        <button onClick={this.handleDecrement}>decrement</button>

        <h2>{this.state.stepCount}</h2>
        <button onClick={this.handleIncrementStep}>Step increment</button>
        <button onClick={this.handleIncrementStep}>Step decrement</button>
      </div>
    );
  }
}

export default Counter;
