import React from "react";

class MyInputComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: "",
      addedValues: [],
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputValue: event.target.value });
  };

  handleAddValue = () => {
    const { inputValue, addedValues } = this.state;
    if (inputValue.trim() !== "")
      this.setState({
        addedValues: [...addedValues, inputValue],
        inputValue: "",
      });
  };

  render() {
    const { inputValue, addedValues } = this.state;
    return (
      <div>
        <h1>{inputValue}</h1>
        <input type="text" value={inputValue} onChange={this.handleInputChange} />
        <button onClick={this.handleAddValue}>Add</button>
        <p>Added values: {addedValues.join(", ")}</p>
      </div>
    );
  }
}

export default MyInputComponent;
