import React from "react";

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      stepCount: 0,
    };
  }

  handleClick = () => {
    this.setState({ count: this.state.count + 1 });
  };

  handleSeptClick = () => {
    this.setState({ stepCount: this.state.stepCount + 10 });
  };

  render() {
    return (
      <div>
        <h2>Count: {this.state.count}</h2>
        <button onClick={this.handleClick}>Add count</button>
        <button>decrement</button>
        <h1>StepCount: {this.state.stepCount}</h1>
        <button onClick={this.handleSeptClick}>Step Increment</button>
        <button>Decremen Step</button>
      </div>
    );
  }
}

export default MyComponent;
