import React, { useState } from "react";
import "./TodoList.css";
import Exzamen from "./Exzamen";

function TodoList() {
  const [todayList, setTodayList] = useState([
    { id: 1, text: "Go to course", completed: false },
    { id: 2, text: "Read a book", completed: true },
    { id: 3, text: "Read a book", completed: false },
  ]);
  const [featureList, setFeatureList] = useState([
    { id: 1, text: "Go to Paris" },
    { id: 2, text: "Go to Barselona" },
  ]);
  const [title, setTitle] = useState("");

  const addTodo = () => {
    if (title.trim() !== "") {
      const newTodo = { id: Date.now(), text: title };

      setTodayList([...todayList, newTodo]);
      setTitle("");
    }
  };

  const toggleCompleted = (id) => {
    const updateList = todayList.map((item) => {
      if (item.id === id) {
        return { ...item, completed: !item.completed };
      }
      return item;
    });
    setTodayList(updateList);
  };

  const deleteTodo = (id) => {
    const updatelist = todayList.filter((item) => item.id !== id);
    setTodayList(updatelist);
  };

  function addAfterPlan() {
    if (title.trim() !== "") {
      const newPlan = { id: Date.now(), text: title };
      setFeatureList([...featureList, newPlan]);
      setTitle("");
    }
  }

